import React, { Component } from 'react';
import { Redirect } from 'react-router-dom';
import { updateUsername } from './store';

class Login extends Component {
    constructor(props) {
      super(props);
      this.state = {
        username: '',
        infoMessage: '',
        color: 'black',
        isLoggedIn: false,
      };
      this.onChange = this.onChange.bind(this);
      this.login = this.login.bind(this);
    }

    onChange(event) {
      this.setState({ username: event.target.value, infoMessage: '' });
    }

    login(event) {
      event.preventDefault();

      let regex = /^[\s\wÅÄÖåäö-]{1,12}$/;
      if (regex.test(this.state.username)) {
        updateUsername(this.state.username);
        this.setState({ isLoggedIn: true });
      }
      else if (this.state.username === '') {
        this.setState({ infoMessage: 'Please provide a username :-)', color: 'red' });
      }
      else {
        this.setState({ infoMessage: 'The username should be between 1-12 characters (or numbers) long and may contain "-" or "_"', color: 'red' });
      }
    }

    render() {
        if (this.state.isLoggedIn === true) {
            return <Redirect to='/Chat' />
          }
        return (
            <div className="login__page" >
              <form onSubmit={this.props.setUsername}>
                <h3>Whats your Username? </h3>
                <input
                  type="text"
                  placeholder="Username"
                  onChange={this.onChange} 
                  value={this.state.username} 
                  onKeyDown={this.props.handelKeyPress}

                  className="form-control" />
              </form>
              <small className="error">{this.props.usernameError}
                </small>
            </div>
        );
    }
}

export default Login
