import React, { Component } from 'react';
import { Redirect } from 'react-router-dom';
import axios from 'axios';
import socketIOClient from "socket.io-client";

import '../App.css';
import { username$, removeUsername } from './store';
import Rooms from './Rooms';


class Chat extends Component {
    constructor(props) {
        super(props);
        this.state = {
            username: username$.value,
            usernameError: "",
            message: '',
            messages: [],
            users: [],
            isLoggedIn: true,
            showMenu: false,
            date: new Date(),
        };
        this.sendMessage = this.sendMessage.bind(this);
        this.logoutButton = this.logoutButton.bind(this);
        this.showMenu = this.showMenu.bind(this);

    }
    ////////////////// END OF constructor ////////////////

    componentDidMount() {
        this.socket = socketIOClient('http://localhost:8000');
        this.socket.on('connection', function () {
        });


        this.socket.on('messages', (messageArray) => {
            this.setState({ messages: messageArray });


        });


        this.socket.on("new_message", (message) => {
            if (!this.state.users.includes(message.username)) {
                this.setState({ users: [...this.state.users, message.username] });
            }
            this.setState({
                messages: [...this.state.messages, message],
            })
            console.log(message);
        })
        this.updateMessages(this.props.match.params.name);
        console.log(this.props.match.params.name);
        this.scrollToBottom();
    }
    componentDidUpdate() {
        this.scrollToBottom();
    }

    componentWillUnmount() {
        this.socket.disconnect();
        this.socket = null;
    }

    componentWillReceiveProps(nextProps) {
        this.updateMessages(nextProps.match.params.name);
    }
    updateMessages(roomName) {
        this.setState({ messages: [] });
        axios.get(`http://localhost:8000/rooms/${roomName}/messages`)
            .then((response) => {
                let users = [];
                let messages = response.data;
                console.log(response.data);

                for (let message of messages) {
                    if (!users.includes(message.username)) {
                        users.push(message.username);
                    }
                }
                this.setState({ users: users, messages: response.data });
            });
    }

    sendMessage = (event) => {
        event.preventDefault();
        if (this.state.message.length > 200) {
            this.setState({ infoMessage: 'Your message can only be between 1-200 characters long' })
            return;
        }
        else if (this.state.message.length === 0) {
            this.setState({ infoMessage: 'Field is empty :-)' });
            return;
        }
        else if (this.state.message.length > 0) {
            this.setState({ message: '' });
        }

        this.socket.emit('send_message', {
            room: this.props.match.params.name,
            username: this.state.username,
            content: this.state.message,
        }, (response) => {
            this.setState({ messages: [...this.state.messages, response.data.newMessage] });
        });
    }

    ///////////////////////////////////////////////////
    ///////////////// Logout  /////////////////
    ///////////////////////////////////////////////////
    logoutButton(e) {
        this.setState({ isLoggedIn: false });
        this.showMenu();
        removeUsername();
    }
    ///////////////////////////////////////////////////
    ///////////////// DropDown  /////////////////
    ///////////////////////////////////////////////////
    showMenu() {
        if (!this.state.popupVisible) {
            // attach/remove event handler
            document.addEventListener('click', this.handleOutsideClick, false);
        } else {
            document.removeEventListener('click', this.handleOutsideClick, false);
        }

        this.setState(prevState => ({
            popupVisible: !prevState.popupVisible,
        }));
    }
    scrollToBottom = () => {
        this.messagesEnd.scrollIntoView({ behavior: "smooth" });
        console.log(this.messagesEnd.scrollIntoView());

    }
    render() {
        if (!username$.value) {
            return <Redirect to='/' />
        }
        return (
            // <h4 className="header__lesson">Mandatory Exercise 1 - Advanced JavaScript with React</h4>
            <div className="container-fluid h-100">
                <Rooms />
                <div className="row justify-content-center h-100">
                    <div className="col-md-12 col-xl-12 chat">
                        <div className="card card-message" >
                            <div className="card-header msg_head">
                                <div className="d-flex bd-highlight">
                                    <div className="user_info">
                                        <span> Loggad in as: {this.state.username} </span>
                                    </div>
                                </div>
                            </div>
                            <div className="card-body msg_card_body" id="card-bodyId">
                                <ul className="msg_cotainer">
                                    {this.state.messages.map((message, key) => {
                                        return (
                                            <li
                                                className={`message ${
                                                    message.username === this.state.username ? "current-user" : "other-users"
                                                    }`}
                                                key={key}>
                                                <p className="name__letter">{message.username.charAt(0)}</p>
                                                <div className="chat__text">
                                                    <span className="user__name" >{message.username}</span>
                                                    <span className="message__content">{message.content}</span>
                                                    <span className="msg_time">{new Date(message.timestamp).toLocaleString("sv-SE")}</span>

                                                </div>
                                            </li>
                                        );
                                    })}
                                    <div style={{ float: "left", clear: "both" }}
                                        ref={(el) => { this.messagesEnd = el; }}>
                                    </div>
                                </ul>
                            </div>
                            <div className="card-footer">
                                <div className="input-group">
                                    <div className="input-group-append">
                                        <button type="button" className="input-group-text attach_btn" >
                                            <i className="far fa-smile"></i></button>
                                    </div>
                                    <form onSubmit={this.sendMessage}>
                                        <input
                                            type="text"
                                            required
                                            value={this.state.message}
                                            onChange={ev => this.setState({ message: ev.target.value })}
                                            style={{ top: 0, left: 0 }}
                                            maxLength={200}
                                            className="type_msg form-control "
                                            placeholder="Type your message..."></input>
                                        <div className="input-group-append">
                                            <button type='submit' className="input-group-text send_btn"><i className="fas fa-location-arrow"></i></button>
                                        </div>
                                    </form>
                                </div>
                            </div>

                        </div>
                    </div>
                </div>
                <div className="row justify-content-center h-100">
                    <div className="col-md-12 col-xl-12 chat">
                        <div className="card card-users" >
                            <div className="card-header msg_head">
                                <div className="d-flex bd-highlight">
                                    <div className="user_info">
                                        <span>Users</span>
                                    </div>
                                </div>
                                <div className="dropdown">
                                    <div className="popover-container" ref={node => { this.node = node; }}>
                                        <button className="dropbtn" onClick={this.showMenu}><i className="fas fa-ellipsis-v"></i></button>
                                        {this.state.popupVisible && (
                                            <div
                                                className="popover"
                                            ><div className="dropdown-content">
                                                    <a href="#"><i className="fas fa-home"></i>   Home</a>
                                                    <a href="#"><i className="fas fa-cogs"></i>  Settings</a>
                                                    <a onClick={this.logoutButton} className="logout__button"><i className="fas fa-sign-out-alt"></i>  Logout [{this.state.username}]</a>
                                                </div>
                                            </div>
                                        )}

                                    </div>

                                </div>
                            </div>
                            <div className="card-body msg_card_body" id="card-bodyId">
                                <div className='users-container'>
                                    <ul className='users-list'>
                                        {this.state.users.map(user => {
                                            return <li className='user-list-item' key={user}>{user}</li>
                                        })}
                                    </ul>
                                </div>
                            </div>
                            <div className="card-footer" style={{ height: 84 }}>

                            </div>

                        </div>

                    </div>
                </div>
            </div>

        )
    }
}

export default Chat;
