import React, { Component } from 'react';
import { Redirect } from 'react-router-dom';
import { updateUsername } from './store';
import '../App.css';
class Login extends Component {
    constructor(props) {
      super(props);
      this.state = {
        username: '',
        infoMessage: '',
        color: 'black',
        isLoggedIn: false,
      };
      this.onChange = this.onChange.bind(this);
      this.login = this.login.bind(this);
      this.handelKeyPress = this.handelKeyPress.bind(this);
    }
    handelKeyPress(e) {
        console.log(this.state.username);
        if (e.keyCode === 13) {
          e.preventDefault();
          if (this.state.username.length > 12) {
            this.setState({ usernameError: "Username must be less 12 characters." });
          } else if (this.state.username.match(/[^a-z0-9_ -]/i)) {
            this.setState({ usernameError: "The username can only contain alphanumeric characters, “-”, “_” and spaces" });
          } else {
            this.setState({ hideLogin: true, usernameError: "" });
          }
        }

      }
    onChange(event) {
      this.setState({ username: event.target.value, infoMessage: '' });
    }

    login(event) {
      event.preventDefault();

      let regex = /^[\s\wÅÄÖåäö-]{1,12}$/;
      if (regex.test(this.state.username)) {
        updateUsername(this.state.username);
        this.setState({ isLoggedIn: true });
      }
      else if (this.state.username === '') {
        this.setState({ infoMessage: 'Please provide a username :-)', color: 'red' });
      }
      else {
        this.setState({ infoMessage: 'The username should be between 1-12 characters (or numbers) long and may contain "-" or "_"', color: 'red' });
      }
    }

    render() {
        if (this.state.isLoggedIn === true) {
            return <Redirect to='/api/rooms' />
          }
        return (
            <div className="login__page" >
              <form onSubmit={this.props.setUsername}>
                <h3>Whats your Username? </h3>
                <input
                  type="text"
                  placeholder="Username"
                  onChange={this.onChange} 
                  value={this.state.username} 
                  onKeyDown={this.props.handelKeyPress}

                  className="form-control login-input" />
                       <button className='login-button' type='submit' onClick={this.login}>Login</button>
              </form>
              <small className="error">{this.props.usernameError}
                </small>
            </div>
        );
    }
}

export default Login
