import React, { useState, useEffect } from 'react';
import SidebarItem from './SidebarItem';
import { NavLink } from 'react-router-dom';
import axios from 'axios';




function Rooms() {
    const [data, setData] = useState({
        rooms: [],
        newRoom: '',
    });

    const createRoom = (event) => {
        event.preventDefault();
        axios.post('http://localhost:8000/rooms', {
            name: data.newRoom
        })
            .then(() => {
                setData({
                    rooms: [...data.rooms],
                    newRoom: ''
                })
                loadRooms();
            })
    }

    const loadRooms = () => {
        return axios.get('http://localhost:8000/rooms')
            .then(response => setData({
                rooms: response.data
            }))

    }
    const deleteRoom = (name) => {
        axios.delete('http://localhost:8000/rooms/' + name)
            .then(() => {
                loadRooms();        
            })
    }
    useEffect(() => {
        loadRooms();
    }, []);

    return (
        <div className="row justify-content-center h-100">
            <div className="col-md-12 col-xl-12 chat">
                <div className="card card-rooms" >
                    <div className="card-header msg_head">
                        <div className="d-flex bd-highlight">
                            <div className="user_info">
                                <span>Rooms</span>
                            </div>
                        </div>
                    </div>
                    <div className="card-body msg_card_body" id="card-bodyId">
                        <div className='navbar-menu-container'>
                            <ul className='navbar-menu'>
                                {data.rooms.map(room => {
                                         <li className='room' key={room}>
                                         <NavLink to={'/rooms/'+ room}>{room}</NavLink>
                                             <button className='remove-room-button' onClick={() => deleteRoom(room.name)}><i className="fas fa-trash"></i></button>
                                         </li>

                                 // return <SidebarItem room={room} key={room.name} onDelete={() => loadRooms()} />
                                })}
                            </ul>
                        </div>
                    </div>
                    <div className="card-footer">
                        <div className="input-group">
                            <form onSubmit={createRoom}>
                                <input
                                    type="text"
                                    placeholder="Room"
                                    required
                                    value={data.newRoom}
                                    onChange={event => setData({ ...data, newRoom: event.target.value })}
                                    style={{ top: 0, left: 0 }}
                                    spellCheck='false'
                                    maxLength={10}
                                    className="type_msg form-control form-control-rooms " placeholder="Create a room..."></input>
                                <div className="input-group-append">
                                    <button type='submit' className="input-group-text send_btn"><i className="fas fa-plus"></i></button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>

    );
}

export default Rooms;
